import React, { useState, useEffect } from 'react';
import './css/main.css';
import logo from './images/logo.png';
import { Link } from 'react-router-dom';

const Header = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isMobile, setIsMobile] = useState(false);

    useEffect(() => {
        // Проверяем размер экрана при загрузке и изменении размера
        const handleResize = () => {
            if (window.innerWidth <= 768) {
                setIsMobile(true);  // Мобильное устройство
            } else {
                setIsMobile(false);  // Компьютер
            }
        };

        // Инициализация при загрузке
        handleResize();

        // Добавление обработчика изменения размера экрана
        window.addEventListener('resize', handleResize);

        // Очистка обработчика при размонтировании компонента
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (
        <header className="header">
            <div className="header-logo">
                <img className="img" src={logo} alt="Лого" style={{ height: '20px' }} />
                <Link to='/' className="header-title">Веб-застосунок</Link>
            </div>

            <div className="header-right">
                {isMobile && (
                    <button className="menu-toggle" onClick={toggleMenu}>
                        <span className="hamburger"></span>
                        <span className="hamburger"></span>
                        <span className="hamburger"></span>
                    </button>
                )}
                <div className={`menu ${isMenuOpen ? 'open' : ''}`}>
                    <Link to='/about'>Про нас</Link>
                    <Link to='/reading'>Reading</Link>
                    <Link to='/listening'>Listening</Link>
                    <Link to='/vocabulary'>Vocabulary</Link>
                    <Link to='/skills'>Skills</Link>
                </div>
            </div>
        </header>
    );
}

export default Header;
