import React, { useEffect, useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import AuthContext from '../context/AuthContext'; 

const Profile = () => {
    const navigate = useNavigate();
    const { isAuthenticated, logout } = useContext(AuthContext);
    const [sections, setSections] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        document.title = "Сторінка профілю";

        if (!isAuthenticated) {
            alert('Для перегляду сторінки профілю Вам необхідно виконати авторизацію.');
            navigate('/login');
            return;
        }

        const fetchProfile = async () => {
            try {
                const accessToken = localStorage.getItem('access_token');
                const response = await axios.get('http://127.0.0.1:8000/api/get_user', {
                    headers: {
                        Authorization: `Bearer ${accessToken}`
                    }
                });
                setSections(response.data);
            } catch (err) {
                if (err.response && err.response.status === 401) {

                    try {
                        const refreshToken = localStorage.getItem('refresh_token');
                        const refreshResponse = await axios.post('http://127.0.0.1:8000/api/token/refresh/', {
                            refresh: refreshToken
                        });
                        const newAccessToken = refreshResponse.data.access;
                        localStorage.setItem('access_token', newAccessToken);

                        const retryResponse = await axios.get('http://127.0.0.1:8000/api/get_user', {
                            headers: {
                                Authorization: `Bearer ${newAccessToken}`
                            }
                        });
                        setSections(retryResponse.data);
                    } catch (refreshError) {
                        logout();
                        alert('Сесія закінчилася. Увійдіть ще раз.');
                        navigate('/login');
                    }
                } else {
                    setError('Не вдалося завантажити дані.');
                }
            }
        };

        fetchProfile();
    }, [isAuthenticated, navigate]);

    return (
        <div className="main-text" style={{ width: "50%", textAlign: "center", margin: "0 auto" }}>
            <h2 style={{ textAlign: "center" }}>Головна сторінка профілю</h2>
            <h3 style={{ textAlign: "center" }}>
                Вітаємо, {sections.first_name} {sections.last_name}!
            </h3>
            <div className='main-text' style={{ textAlign: "left", margin: "0 auto" }}>
                <p><strong>Електронна адреса:</strong> {sections.email}</p>
                <p><strong>Ім'я:</strong> {sections.first_name}</p>
                <p><strong>Прізвище:</strong> {sections.last_name}</p>
                <Link to="/" className="btn btn-primary">Перейти до тестування</Link>
            </div>
            
        </div>
    );
};

export default Profile;
