import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Profile = () => {
    
    const navigate = useNavigate();
    const isAuthenticated = !!localStorage.getItem('access_token');
    const [sections, setSections] = useState([]);

    useEffect(() => {
        document.title = "Сторінка профілю";

        if (!isAuthenticated) {
            alert('Для перегляду сторінки профілю Вам необхідно виконати авторизацію.');
            navigate('/login'); 
            return;
        }

        axios.get('http://127.0.0.1:8000/api/get_user')
        .then((response) => {
            setSections(response.data);
            setLoading(false);
        })
        .catch(() => {
            setLoading(false);
            setError('Не вдалося завантажити дані.');
        });

    }, []);

    return (
        <div>
            
            <h2 style={{ textAlign: "center" }}>Головна сторінка профілю</h2>
            <h3 style={{ textAlign: "center" }}>Вітаємо, {sections.username}!</h3>
            <h4 style={{ textAlign: "center" }}>Ваші досягнення:</h4>   
           
        </div>
    );
}

export default Profile;
