import React, { useState, useEffect } from "react";

const Times = () => {
  const [presentTimes, setPresentTimes] = useState([]);
  const [pastTimes, setPastTimes] = useState([]);
  const [futureTimes, setFutureTimes] = useState([]);
  const [selectedTime, setSelectedTime] = useState(null); // Для отображения информации

  // Функция для загрузки данных с API
  const fetchTimesData = async () => {
    try {
      const presentResponse = await fetch("http://127.0.0.1:8000/api/present-time/");
      const presentData = await presentResponse.json();
      setPresentTimes(presentData);

      const pastResponse = await fetch("http://127.0.0.1:8000/api/past-time/");
      const pastData = await pastResponse.json();
      setPastTimes(pastData);

      const futureResponse = await fetch("http://127.0.0.1:8000/api/future-time/");
      const futureData = await futureResponse.json();
      setFutureTimes(futureData);
    } catch (error) {
      console.error("Error fetching times data:", error);
    }
  };

  // Загружаем данные при монтировании компонента
  useEffect(() => {
    fetchTimesData();
  }, []);

  // Функция для отображения информации по клику
  const handleImageClick = (time) => {
    setSelectedTime(time);
  };

  return (
    <div>
      <h1>Times</h1>

      <section>
        <h2>Present Time</h2>
        <div className="time-list">
          {presentTimes.map((time) => (
            <div key={time.id} className="time-item" onClick={() => handleImageClick(time)}>
              {time.image && <img src={time.image} alt={time.name} />}
            </div>
          ))}
        </div>
      </section>

      <section>
        <h2>Past Time</h2>
        <div className="time-list">
          {pastTimes.map((time) => (
            <div key={time.id} className="time-item" onClick={() => handleImageClick(time)}>
              {time.image && <img src={time.image} alt={time.name} />}
            </div>
          ))}
        </div>
      </section>

      <section>
        <h2>Future Time</h2>
        <div className="time-list">
          {futureTimes.map((time) => (
            <div key={time.id} className="time-item" onClick={() => handleImageClick(time)}>
              {time.image && <img src={time.image} alt={time.name} />}
            </div>
          ))}
        </div>
      </section>

      {selectedTime && (
        <div className="time-details">
          <h3>{selectedTime.name}</h3>
          {selectedTime.image && <img src={selectedTime.image} alt={selectedTime.name} />}
          <p>{selectedTime.description}</p>
          <p><strong>Formula:</strong> {selectedTime.formula}</p>
          <p><strong>Examples:</strong> {selectedTime.examples}</p>
          <button onClick={() => setSelectedTime(null)}>Close</button>
        </div>
      )}
    </div>
  );
};

export default Times;
