import { useEffect, useState } from 'react';
import axios from 'axios';
import '../css/main.css';

const Reading = () => {
  const [sections, setSections] = useState([]);
  const [selected, setSelected] = useState(null);
  const [current, setCurrent] = useState(0);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isConfirmed, setIsConfirmed] = useState(false);
  const [answersHistory, setAnswersHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const increasing_score = 1;
  const isAuthenticated = !!localStorage.getItem('access_token');

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/section/?category=reading')
      .then((response) => {
        setSections(response.data);
        setLoading(false);
      })
      .catch(() => {
        setLoading(false);
        setError('Не вдалося завантажити дані.');
      });
  }, []);

  const handleConfirm = () => {
    if (!selected || !selected.questions) return;
    
    const correct = selected.questions[current].answers.indexOf(selected.questions[current].correct);
    if (selectedAnswer === correct) {
      setScore(score + selected.questions[current].number_points);
    }
    setAnswersHistory([
      ...answersHistory,
      {
        question: selected.questions[current].question,
        selected: selectedAnswer,
        correct: correct,
        answers: selected.questions[current].answers,
      },
    ]);
    setIsConfirmed(true);
  };

  const handleNext = () => {
    if (!selected || !selected.questions) return;

    const next = current + 1;
    if (next < selected.questions.length) {
      setCurrent(next);
      setSelectedAnswer(null);
      setIsConfirmed(false);
    } else {
      setShowResult(true);
    }
  };

  const reset = () => {
    setSelected(null);
    setCurrent(0);
    setScore(0);
    setShowResult(false);
    setSelectedAnswer(null);
    setIsConfirmed(false);
    setAnswersHistory([]);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return (
      <div className="center-text">
        <h2>{error}</h2>
        <button onClick={reset} style={{ marginTop: '20px' }}>
          Попробовать снова
        </button>
      </div>
    );
  }

  return (
    <div>
      <main className="main-text" style={{ width: '95%', justifyContent: 'center', margin: '0 auto' }}>
        {!selected && sections.length > 0 && (
          <div className="center-text">
            <h2>Оберіть тест з читання, який бажаєте пройти</h2>
            {sections.map((section) => (
              <div
                key={section.id}
                onClick={() => {
                  if (!section.public_test && !isAuthenticated) {
                    alert('Цей тест доступний лише для авторизованих користувачів.');
                    return;
                  }
                  setSelected(section);
                }}
                style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  margin: '10px',
                  cursor: 'pointer',
                  textAlign: 'center',
                }}
              >
                {section.image && (
                  <img
                    src={`${section.image}`}
                    alt={section.name}
                    style={{ width: '65%', height: "250px", marginBottom: '10px' }}
                  />
                )}
              </div>
            ))}
          </div>
        )}

        {selected && !showResult && selected.questions && (
          <div>
            <h3>{selected.name}</h3>
            {selected.description.split(/\n/g).map((paragraph, idx) => (
              <p key={idx}>{paragraph.trim()}</p>
            ))}
            <hr style={{ margin: '20px 0' }} />
            <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
              <h3>{selected.questions[current].question}</h3>
              {selected.questions[current].answers.map((a, i) => {
                let backgroundColor = '';
                if (isConfirmed) {
                  if (i === selected.questions[current].answers.indexOf(selected.questions[current].correct)) {
                    backgroundColor = '#c8e6c9';
                  } else if (i === selectedAnswer) {
                    backgroundColor = '#ffcdd2';
                  }
                } else if (i === selectedAnswer) {
                  backgroundColor = '#d3eaff';
                }

                return (
                  <li key={i} style={{ marginBottom: '10px' }}>
                    <button
                      disabled={isConfirmed}
                      onClick={() => setSelectedAnswer(i)}
                      style={{
                        padding: '10px',
                        width: '100%',
                        textAlign: 'left',
                        cursor: isConfirmed ? 'default' : 'pointer',
                        backgroundColor,
                        borderRadius: '5px',
                        border: '1px solid #ccc',
                      }}
                    >
                      {a}
                    </button>
                  </li>
                );
              })}
            </ul>

            {!isConfirmed && selectedAnswer !== null && (
              <button onClick={handleConfirm} className="confirm-btn" style={{ marginBottom: '10px' }}>
                Підтвердити відповідь
              </button>
            )}

            {isConfirmed && selectedAnswer !== selected.questions[current].answers.indexOf(selected.questions[current].correct) && (
              <div style={{ color: 'red', marginTop: '10px', marginBottom: '10px' }}>
                {selected.questions[current].errorText}
              </div>
            )}

            {isConfirmed && (
              <button onClick={handleNext} className="next-btn" style={{ marginBottom: '10px' }}>
                Наступне питання →
              </button>
            )}
          </div>
        )}

        {showResult && (
          <div className="center-text">
            <h3>Результати тесту</h3>
            <p>Ви набрали {score} з {increasing_score * selected.questions.length} балів</p>

            <table style={{ width: '100%', marginTop: '20px', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th>№</th>
                  <th>Питання</th>
                  <th>Ваша відповідь</th>
                  <th>Правильна відповідь</th>
                  <th>Кількість балів</th>
                </tr>
              </thead>
              <tbody>
                {answersHistory.map((entry, index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{entry.question}</td>
                    <td>{entry.answers[entry.selected]}</td>
                    <td>{entry.answers[entry.correct]}</td>
                    <td>{entry.selected === entry.correct ? '1' : '0'}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            <button onClick={reset} style={{ marginTop: '20px' }}>
              ← До вибору розділу
            </button>
          </div>
        )}
      </main>
    </div>
  );
};

export default Reading;
