import React, { useState, useEffect } from 'react';
import './css/header.css';
import logo from './images/logo.png';
import { Link, useNavigate } from 'react-router-dom';

const Header = () => {
    const [isOpen, setOpen] = useState(false);
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [username, setUsername] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('access_token');
        if (token) {
            setIsAuthenticated(true);

            // Парсим токен и достаём имя пользователя
            try {
                const payload = JSON.parse(atob(token.split('.')[1]));
                setUsername(payload.username); // или 'user', зависит от структуры
            } catch (e) {
                console.error('Помилка декодування токена:', e);
                setUsername('');
            }
        } else {
            setIsAuthenticated(false);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        setIsAuthenticated(false);
        setUsername('');
        navigate('/login');
    };

    return (
        <header className="header">
            <div className="header-logo">
                <img className="img" src={logo} alt="Лого" />
                <Link to='/' className="header-title">Веб-застосунок</Link>
            </div>

            <nav className={`header-nav ${isOpen ? "active" : ""}`}>
                <Link to='/vocabulary'>Vocabulary</Link>
                <Link to='/skills'>Skills</Link>
                <Link to='/grammar'>Grammar</Link>
                {!isAuthenticated ? (
                    <>
                        <Link to='/login'>Login</Link>
                        <Link to='/register'>Register</Link>
                    </>
                ) : (
                    <>
                        <p>Hello, {username}!</p>
                        <button onClick={handleLogout} className="logout-button">Logout</button>
                    </>
                )}
            </nav>

            <button className='header-button' onClick={() => setOpen(!isOpen)}>
                ☰
            </button>
        </header>
    );
};

export default Header;
