import React, { useState, useEffect } from 'react';
import './css/main.css';
import logo from './images/logo.png';
import { Link } from 'react-router-dom';

const Header = () => {
    const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
    const [menuOpen, setMenuOpen] = useState(false);

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth < 768);
        };
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const toggleMenu = () => setMenuOpen(!menuOpen);

    return (
        <header className="header">
            <div className="header-left">
                <img className="img" src={logo} alt="Лого" style={{ height: '20px' }} />
                <Link to='/'>Веб-застосунок</Link>
            </div>

            {isMobile ? (
                <div className="mobile-menu">
                    <button className="menu-toggle" onClick={toggleMenu}>
                        ☰
                    </button>
                    {menuOpen && (
                        <div className="mobile-menu-items">
                            <Link to='/about' onClick={() => setMenuOpen(false)}>Про нас</Link>
                            <Link to='/reading' onClick={() => setMenuOpen(false)}>Reading</Link>
                            <Link to='/listening' onClick={() => setMenuOpen(false)}>Listening</Link>
                            <Link to='/vocabulary' onClick={() => setMenuOpen(false)}>Vocabulary</Link>
                            <Link to='/skills' onClick={() => setMenuOpen(false)}>Skills</Link>
                        </div>
                    )}
                </div>
            ) : (
                <div className="header-right">
                    <Link to='/about'>Про нас</Link>
                    <Link to='/reading'>Reading</Link>
                    <Link to='/listening'>Listening</Link>
                    <Link to='/vocabulary'>Vocabulary</Link>
                    <Link to='/skills'>Skills</Link>
                </div>
            )}
        </header>
    );
};

export default Header;
