import { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/main.css';

const Vocabulary = () => {
  const [quiz, setQuiz] = useState(null);
  const [current, setCurrent] = useState(0);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isConfirmed, setIsConfirmed] = useState(false);
  const [answersHistory, setAnswersHistory] = useState([]);

  useEffect(() => {
    // Запрос на получение данных викторины из Django
    axios.get('http://localhost:8000/app_backend/api/quiz/')
      .then(response => {
        setQuiz(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the quiz data!", error);
      });
  }, []);

  if (!quiz) {
    return <div>Загрузка...</div>;
  }

  const handleConfirm = () => {
    const correct = quiz.questions[current].correct;
    if (selectedAnswer === correct) {
      setScore(score + 1);
    }
    setAnswersHistory([
      ...answersHistory,
      {
        question: quiz.questions[current].question,
        selected: selectedAnswer,
        correct: correct,
        answers: quiz.questions[current].answers,
      },
    ]);
    setIsConfirmed(true);
  };

  const handleNext = () => {
    const next = current + 1;
    if (next < quiz.questions.length) {
      setCurrent(next);
      setSelectedAnswer(null);
      setIsConfirmed(false);
    } else {
      setShowResult(true);
    }
  };

  const reset = () => {
    setCurrent(0);
    setScore(0);
    setShowResult(false);
    setSelectedAnswer(null);
    setIsConfirmed(false);
    setAnswersHistory([]);
  };

  return (
    <div>
      <main className="main-text">
        {!showResult && (
          <div>
            <h3>{quiz.title}</h3>
            <p>{quiz.preview}</p>
            <hr style={{ margin: '20px 0' }} />

            <h3>{quiz.questions[current].question}</h3>
            <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
              {quiz.questions[current].answers.map((a, i) => {
                let backgroundColor = '';
                if (isConfirmed) {
                  if (i === quiz.questions[current].correct) {
                    backgroundColor = '#c8e6c9'; // green
                  } else if (i === selectedAnswer) {
                    backgroundColor = '#ffcdd2'; // red
                  }
                } else if (i === selectedAnswer) {
                  backgroundColor = '#d3eaff'; // selected
                }

                return (
                  <li key={i} style={{ marginBottom: '10px' }}>
                    <button
                      disabled={isConfirmed}
                      onClick={() => setSelectedAnswer(i)}
                      style={{
                        padding: '10px',
                        width: '100%',
                        textAlign: 'left',
                        cursor: isConfirmed ? 'default' : 'pointer',
                        backgroundColor,
                        borderRadius: '5px',
                        border: '1px solid #ccc',
                      }}
                    >
                      {a}
                    </button>
                  </li>
                );
              })}
            </ul>

            {!isConfirmed && selectedAnswer !== null && (
              <button onClick={handleConfirm} className="confirm-btn">
                Підтвердити відповідь
              </button>
            )}

            {isConfirmed && selectedAnswer !== quiz.questions[current].correct && (
              <div style={{ color: 'red', marginTop: '10px', marginBottom: '10px' }}>
                Неправильный ответ!
              </div>
            )}

            {isConfirmed && (
              <button onClick={handleNext} className="next-btn">
                Следующий вопрос →
              </button>
            )}
          </div>
        )}

        {showResult && (
          <div className="center-text">
            <h3>Результаты теста</h3>
            <p>Вы набрали {score} из {quiz.questions.length} баллов</p>

            <table style={{ width: '100%', marginTop: '20px', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th>№</th>
                  <th>Вопрос</th>
                  <th>Ваш ответ</th>
                  <th>Правильный ответ</th>
                  <th>Результат</th>
                </tr>
              </thead>
              <tbody>
                {answersHistory.map((entry, index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{entry.question}</td>
                    <td>{entry.answers[entry.selected]}</td>
                    <td>{entry.answers[entry.correct]}</td>
                    <td>{entry.selected === entry.correct ? '✅' : '❌'}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            <button onClick={reset} style={{ marginTop: '20px' }}>
              ← Вернуться к тесту
            </button>
          </div>
        )}
      </main>
    </div>
  );
};

export default Vocabulary;
