import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Profile = () => {
    const navigate = useNavigate();
    const isAuthenticated = !!localStorage.getItem('access_token');
    const refreshToken = localStorage.getItem('refresh_token');
    const [sections, setSections] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Функция для обновления токена
    const refreshAccessToken = async () => {
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/token/refresh/', {
                refresh: refreshToken,
            });
            localStorage.setItem('access_token', response.data.access);
            return response.data.access;
        } catch (err) {
            setError('Не вдалося оновити токен.');
            navigate('/login');
            return null;
        }
    };

    useEffect(() => {
        document.title = "Сторінка профілю";
        
        if (!isAuthenticated) {
            alert('Для перегляду сторінки профілю Вам необхідно виконати авторизацію.');
            navigate('/login');
            return;
        }
        
        const fetchData = async () => {
            let token = localStorage.getItem('access_token');
            
            // Проверка, истёк ли токен
            if (token) {
                try {
                    await axios.get('http://127.0.0.1:8000/api/get_user', {
                        headers: {
                            Authorization: `Bearer ${token}`,
                        },
                    });
                } catch (error) {
                    // Если токен истёк, пытаемся обновить его
                    if (error.response && error.response.status === 401) {
                        token = await refreshAccessToken();
                        if (!token) return;
                    }
                }
            }
            
            // Загружаем данные о пользователе после успешной авторизации
            if (token) {
                axios.get('http://127.0.0.1:8000/api/get_user', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                })
                .then((response) => {
                    setSections(response.data);
                    setLoading(false);
                })
                .catch(() => {
                    setLoading(false);
                    setError('Не вдалося завантажити дані.');
                });
            }
        };

        fetchData();
    }, [isAuthenticated, navigate, refreshAccessToken]);

    return (
        <div>
            <h2 style={{ textAlign: "center" }}>Головна сторінка профілю</h2>
            {loading ? (
                <h3 style={{ textAlign: "center" }}>Завантаження...</h3>
            ) : error ? (
                <h3 style={{ textAlign: "center", color: 'red' }}>{error}</h3>
            ) : (
                <>
                    <h3 style={{ textAlign: "center" }}>Вітаємо, {sections.username}!</h3>
                    <h4 style={{ textAlign: "center" }}>Ваші досягнення:</h4>
                </>
            )}
        </div>
    );
};

export default Profile;
