import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import jwt_decode from 'jwt-decode';

const Profile = () => {
    const navigate = useNavigate();
    const isAuthenticated = !!localStorage.getItem('access_token');
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        document.title = "Сторінка профілю";

        // Проверка авторизации и наличие токена
        if (!isAuthenticated) {
            alert('Для перегляду сторінки профілю Вам необхідно виконати авторизацію.');
            navigate('/login');
            return;
        }

        // Проверка на истечение срока действия access токена
        const accessToken = localStorage.getItem('access_token');
        if (accessToken && isTokenExpired(accessToken)) {
            refreshToken();  // Оновлення токену, якщо access токен застарів
        }

        // Запрос данных пользователя
        axios.get('http://127.0.0.1:8000/api/get_user', {
            headers: { 'Authorization': `Bearer ${accessToken}` }
        })
        .then((response) => {
            setUserData(response.data);
            setLoading(false);
        })
        .catch((err) => {
            setLoading(false);
            setError('Не вдалося завантажити дані.');
        });
    }, [isAuthenticated, navigate]);

    const isTokenExpired = (token) => {
        const decoded = jwt_decode(token);
        return decoded.exp * 1000 < Date.now();
    };

    const refreshToken = async () => {
        const refreshToken = localStorage.getItem('refresh_token');
        if (refreshToken) {
            try {
                const response = await axios.post('http://127.0.0.1:8000/api/token/refresh/', {
                    refresh: refreshToken
                });
                localStorage.setItem('access_token', response.data.access);
                localStorage.setItem('refresh_token', response.data.refresh);
            } catch (error) {
                console.error('Error refreshing token', error);
                alert('Session expired, please log in again.');
                navigate('/login');
            }
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2 style={{ textAlign: "center" }}>Головна сторінка профілю</h2>
            <h3 style={{ textAlign: "center" }}>Вітаємо, {userData?.username}!</h3>
            <h4 style={{ textAlign: "center" }}>Ваші досягнення:</h4>

            {/* Выводим достижения или другие данные профиля */}
            <div style={{ textAlign: "center" }}>
                <p>Імейл: {userData?.email}</p>
                <p>Інші дані: {/* Вставьте дополнительные данные профиля, если есть */}</p>
            </div>
        </div>
    );
};

export default Profile;
