import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ChangeData = () => {
    const navigate = useNavigate();
    const isAuthenticated = !!localStorage.getItem('access_token');
    const [sections, setSections] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        document.title = "Зміна даних";

        if (!isAuthenticated) {
            alert('Для перегляду сторінки профілю Вам необхідно виконати авторизацію.');
            navigate('/login');
            return;
        }

        const fetchProfile = async () => {
            try {
                const accessToken = localStorage.getItem('access_token');
                const response = await axios.get('http://127.0.0.1:8000/api/get_user', {
                    headers: {
                        Authorization: `Bearer ${accessToken}`
                    }
                });
                setSections(response.data);
            } catch (err) {
                if (err.response && err.response.status === 401) {

                    try {
                        const refreshToken = localStorage.getItem('refresh_token');
                        const refreshResponse = await axios.post('http://127.0.0.1:8000/api/token/refresh/', {
                            refresh: refreshToken
                        });
                        const newAccessToken = refreshResponse.data.access;
                        localStorage.setItem('access_token', newAccessToken);

                        const retryResponse = await axios.get('http://127.0.0.1:8000/api/get_user', {
                            headers: {
                                Authorization: `Bearer ${newAccessToken}`
                            }
                        });
                        setSections(retryResponse.data);
                    } catch (refreshError) {
                        localStorage.removeItem('access_token');
                        localStorage.removeItem('refresh_token');
                        alert('Сесія закінчилася. Увійдіть ще раз.');
                        navigate('/login');
                    }
                } else {
                    setError('Не вдалося завантажити дані.');
                }
            }
        };

        fetchProfile();
    }, [isAuthenticated, navigate]);

    return (
        <div className='container'>
            <h1>Авторизація</h1>
            <form className='form' onSubmit={handleSubmit}>
                <label>Впишіть новий логін: </label>
                <input
                    type='text'
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <label>Впишіть новий пароль: </label>
                <input
                    type='password'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <button type='submit'>Змінити дані</button>
            </form>
        </div>
    );
};

export default ChangeData;
